mapper.select:
SELECT NVL(MAX(sort_seq), 0)
FROM code_detail
WHERE group_code = #{groupCode}

SELECT group_code, code_value, code_name, sort_seq, use_yn, reg_date
FROM code_detail
WHERE group_code = #{groupCode} AND code_value = #{codeValue}

mapper.update:
UPDATE code_detail
SET code_name = #{codeName}
WHERE group_code = #{groupCode} AND code_value = #{codeValue}
